custom-style.scss:
// mixin
@import '_mixin';

input:focus,
textarea:focus,
select:focus,
a:focus {
    outline: none;
}

// body 
.body {
    margin-top: 0px; // override css default no css property
}

.custom-navUser {
    @include baseDisplay(8vh, 100%, flex);
    @include baseColor("custom-baseColor");
    align-items: center;
    justify-content: space-between;

    .custom-navUser-section {

        @include baseDisplay(8vh, auto, flex);
        place-items: center;

        margin-left: 0px;
        margin-bottom: 0px;

        margin-left: 10vw;

        li {
            list-style-type: none;
            display: flex;
        }

        li:nth-child(3) {
            margin-left: 1vw;
        }

        button {
            padding: 0px;
        }

        .navPages-action {
            text-align: center;
            color: white;
            font-weight: normal;
        }
    }

    .custom-navUser-section:nth-child(2) {
        margin-left: 0vw;
        margin-right: 10vw;
    }

}

.custom-header-logo {
    width: 100%;
    height: auto;
    display: flex;
    align-items: center;
    justify-content: center;

    margin-top: 10px;

    .inner-custom-header-logo {
        @include baseDisplay(12vh, 77vw, flex);
        position: relative;

        .header-logo__link {
            padding: 0px;
            margin: 0px;

            .header-logo-image-container {
                display: flex;
                align-items: center;
                justify-content: center;
            }
        }

        .custom-search-bar-container {
            @include baseDisplay(12vh, 38vw, flex);
            position: absolute;
            right: 0;
            top: 0;
            display: grid;
            place-items: center;

            .custom-inner-search-bar-container {
                @include baseDisplay(6vh, 38vw, flex);
                display: flex;
                align-items: center;
                justify-content: center;

                .main-search-bar-container {
                    @include baseDisplay(50px, auto, flex);
                    border-radius: 50px;
                    border: 3px solid rgba($color: #189ed3, $alpha: 0.3);
                    display: flex;
                    flex-wrap: wrap;
                    flex-direction: row;
                    align-items: center;
                    justify-content: start;

                    .navPages-quickSearch {
                        margin: 0px;
                        padding: 0px;
                        display: block;
                        height: auto;
                        display: grid;
                        place-items: center;

                        .container {
                            background: none;
                            width: auto;
                            padding: 0px;
                            margin: 0px;

                            margin-left: 1vw;

                            .form {
                                outline: none;
                                margin: 0px;
                                width: 22vw;
                                border: 0px solid transparent;

                                .form-fieldset {
                                    outline: none;

                                    .form-field {
                                        outline: none;
                                        margin: 0px;

                                        .form-input {
                                            outline: none;
                                            border: 0px solid transparent;
                                            border-radius: 50px;
                                        }
                                    }
                                }
                            }

                            .aria-description--hidden {
                                margin: 0px;
                            }

                        }
                    }

                    .search-icon {
                        margin-left: .5vw;
                        margin-right: .5vw;
                        padding-right: .5vw;
                    }

                }

                .cart-logo {
                    margin-left: 1vw;
                    height: 50px;
                }
            }
        }

    }

}

.custom-navPages {
    width: 100%;
    border-top: 2px solid #189ed3;
    // border-top: #189ed3;
    display: grid;
    place-items: center;

    .navPages-list {
        width: auto;

        .navPages-item {

            .navPages-action {
                padding-right: 3.2rem;
                padding-left: 3.2rem;
            }
        }

        .navPages-item:hover {
            background: #189ed3;

            a {
                color: white;
            }
        }
    }
}

.custom-heroCarousel {
    width: 100%;
    height: auto;

    .slick-list {
        height: auto;

        .slick-track {
            height: auto;

            .slick-slide {
                height: auto;

                .heroCarousel-slide {
                    height: auto;

                    .heroCarousel-image-wrapper {
                        position: relative;
                        display: grid;
                        place-items: center;
                        height: auto;

                        .main-banner {
                            position: absolute;
                            z-index: 20;
                            @include baseDisplay(auto, auto, grid);
                            border: 2px solid rgba($color: #ffffff, $alpha: 0.8);
                            display: grid;
                            place-items: center;

                            margin-bottom: 70px;

                            .inner-banner {
                                margin: 7px 7px 7px 7px;
                                @include baseDisplay(230px, 560px, grid);
                                background: rgba($color: #ffffff, $alpha: 0.9);
                                place-items: center;

                                .content {
                                    max-height: 25vh;
                                    display: grid;
                                    place-items: center;

                                    .banner-text-1 {
                                        height: auto;
                                        width: 340px;
                                    }

                                    .banner-text-2 {
                                        margin-top: 10px;
                                        height: auto;
                                        width: 470px;
                                    }

                                    .p-text {
                                        margin-top: 5px;
                                        max-width: 70vw;
                                        text-align: center;
                                        margin-bottom: 15px;
                                        font-size: 12px;
                                    }

                                    .btn-action {
                                        height: 30px;
                                        width: 120px;
                                        background: rgba($color: #ff0077, $alpha: 1.0);
                                        border-radius: 5px;
                                        color: #ffffff;
                                    }
                                }
                            }
                        }

                        .heroCarousel-image {
                            object-fit: contain;
                        }
                    }
                }
            }
        }
    }


}

.custom-text-promotion-label {
    height: auto;
    width: 100%;
    display: grid;
    place-items: center;

    margin-top: 35px; // apply to lower viewport set to 0

    .inner-container {
        height: auto;
        width: 90vw;
        display: grid;
        place-items: center;

        .content {
            display: grid;
            place-items: center;

            .label {
                font-weight: 700;
                font-size: 2.4em;
            }

            .line-container {
                height: .2vh;
                width: 78vw;
                background: #189ed3;
                display: flex;
                align-items: center;
                justify-content: center;

                .line {
                    background: rgba($color: #ff0077, $alpha: 1.0);
                    width: 210px;
                    height: .7vh;
                    margin-bottom: 2px;
                }
            }

            .text-content {
                margin-top: 10px;
                font-weight: normal;
                width: 42vw;
                text-align: center;
                font-size: 1rem;
                word-break: break-all;
            }
        }


    }
}

