.custom-productCarousel {
    margin-top: 0px;
    width: 100%;

    .productCarousel-slide {
        display: grid;
        place-items: center;

        .card {

            .card-figure {

                .card-img-container {
                    height: 180px;

                    .card-image {
                        margin: 0px;
                        height: 180px;
                        padding: 0px 10px 0px 10px;
                    }
                }
            }

            .card-body {
                margin: 0px 0px 20px 0px;
                width: 240px;
                text-align: left;

                display: grid;
                place-items: center;

                .card-title {
                    font-size: 12px;
                    padding: 0px 10px 0px 10px;
                }

                .card-text {
                    padding: 0px 10px 0px 10px;
                }

                .custom-card-text {
                    margin-top: 10px;
                    width: 220px;
                    border: 2px solid rgb(241, 64, 64);
                    height: 45px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    overflow: hidden;

                    .price-container {
                        height: 43px;
                        width: 170px;
                        background: #ffffff;
                        display: flex;
                        align-items: center;
                        justify-content: start;

                        .custom-price-section {
                            margin-left: 15px;
                        }
                    }

                    .icon-cart-container {
                        background: rgb(241, 64, 64);
                        height: 43px;
                        width: 60px;
                        display: grid;
                        place-items: center;

                        .cart-icon {
                            height: auto;
                            width: 20px;
                        }
                    }
                }
            }
        }
    }
}

.parallax-banner {
    background-image: url("../img/custom-img/parallax-banner.jpg");
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
    height: 70vh;
    width: 100%;
    margin-top: 5px;
    display: grid;
    place-items: center;

    .main-banner {
        position: absolute;
        z-index: 20;
        @include baseDisplay(auto, auto, grid);
        border: 2px solid rgba($color: #ffffff, $alpha: 0.9);
        display: grid;
        place-items: center;

        margin-top: 5px; // apply to lower viewport with value of 0
        margin-bottom: 0px;

        .outer-banner {
            @include baseDisplay(auto, auto, grid);
            border: 2px solid rgba($color: #ffffff, $alpha: 0.9);
            display: grid;
            place-items: center;
            margin: 12px 12px 12px 12px;

            .inner-banner {
                @include baseDisplay(280px, 673px, grid);
                margin: 8px 8px 8px 8px;
                background: rgba($color: #ffffff, $alpha: 0.9);
                place-items: center;

