_mixin.scss:
@mixin baseDisplay ($height, $width, $display) {
    display: $display;
    height: $height;
    width: $width;
}

@mixin baseColor($color) {
    background: stencilColor($color);
}

@mixin Color($color) {
    color: stencilColor($color);
}


